mapper.insert:
insert into PAYMENT (
    MATCH_IDX, SELLER_IDX, BUYER_IDX, PAYMENT_METHOD, PAYMENT_AMOUNT,
    PAYMENT_DATE, PAYMENT_STATUS, MERCHANT_UID, IMP_UID
)
values (
    #{match_idx}, #{seller_idx}, #{buyer_idx}, #{payment_method}, #{payment_amount},
    CURRENT_TIMESTAMP, #{payment_status}, #{merchant_uid}, #{imp_uid}
                 );

mapper.select:
SELECT COUNT(*) > 0 FROM PAYMENT P
         LEFT JOIN REFUND R ON P.PAYMENT_IDX = R.PAYMENT_IDX
WHERE P.BUYER_IDX = #{idx}
  AND P.MATCH_IDX = #{match_idx}
  AND R.PAYMENT_IDX IS NULL
